      var close_and_redirect = function() {
        if (opener && !opener.closed) {
          opener.location.href = "http://localhost:9610/done";
        }  
        window.close();
      }
    &lt;/script&gt;
  &lt;/head&gt;
  &lt;body onload="setTimeout('close_and_redirect()', 500)"&gt;
    Yay, logged in! Closing...
